INSERT INTO `wp_comments` VALUES (1, 1, 'Một người bình luận WordPress', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2020-10-23 05:51:54', '2020-10-23 05:51:54', 'Xin chào, đây là một bình luận\nĐể bắt đầu với quản trị bình luận, chỉnh sửa hoặc xóa bình luận, vui lòng truy cập vào khu vực Bình luận trong trang quản trị.\nAvatar của người bình luận sử dụng <a href=\"https://gravatar.com\">Gravatar</a>.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (2, 564, 'Gerhard', 'gerhard@woothemes.com', '', '', '2016-12-29 08:32:06', '2016-12-29 08:32:06', 'Nunc omni virtuti vitium contrario nomine opponitur. Age sane, inquam.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (3, 564, 'Tom McFarlin', 'tom@tommcfarlin.com', '', '', '2016-12-29 08:32:06', '2016-12-29 08:32:06', 'An dolor longissimus quisque miserrimus, voluptatem non optabiliorem diuturnitas facit? Sed quanta sit alias, nunc tantum possitne esse tanta. Scio enim esse quosdam, qui quavis lingua philosophari possint; Inde sermone vario sex illa a Dipylo stadia confecimus.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (4, 565, 'Ryan', 'ryan@woothemes.com', '', '', '2016-12-29 08:32:06', '2016-12-29 08:32:06', 'Quod autem in homine praestantissimum atque optimum est, id deseruit. Apud ceteros autem philosophos, qui quaesivit aliquid, tacet; Sedulo, inquam, faciam. Naturales divitias dixit parabiles esse, quod parvo esset natura contenta. Suo enim quisque studio maxime ducitur.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (5, 565, 'James Koster', 'james@jameskoster.co.uk', '', '', '2016-12-29 08:32:06', '2016-12-29 08:32:06', 'Est enim effectrix multarum et magnarum voluptatum. Si alia sentit, inquam, alia loquitur, numquam intellegam quid sentiat; Et nunc quidem quod eam tuetur, ut de vite potissimum loquar, est id extrinsecus; Ut optime, secundum naturam affectum esse possit.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (6, 565, 'Jared Erickson', 'jared@lessmade.com', '', '', '2016-12-29 08:32:06', '2016-12-29 08:32:06', 'Hanc ergo intuens debet institutum illud quasi signum absolvere. Sed quid attinet de rebus tam apertis plura requirere? Quam illa ardentis amores excitaret sui! Cur tandem? A primo, ut opinor, animantium ortu petitur origo summi boni.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (7, 565, 'Eryn Erickson', 'eryn@lessmade.com', '', '', '2016-12-29 08:32:06', '2016-12-29 08:32:06', 'Ego vero volo in virtute vim esse quam maximam; Nec vero alia sunt quaerenda contra Carneadeam illam sententiam.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (8, 566, 'Gerhard', 'gerhard@woothemes.com', '', '', '2016-12-29 08:32:05', '2016-12-29 08:32:05', 'Videmusne ut pueri ne verberibus quidem a contemplandis rebus perquirendisque deterreantur? Sumenda potius quam expetenda. Eorum enim est haec querela, qui sibi cari sunt seseque diligunt.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (9, 566, 'Coen Jacobs', 'coen@woothemes.com', '', '', '2016-12-29 08:32:05', '2016-12-29 08:32:05', 'Quis non odit sordidos, vanos, leves, futtiles? Nam adhuc, meo fortasse vitio, quid ego quaeram non perspicis. Nosti, credo, illud. Nemo pius est, qui pietatem. Hoc mihi cum tuo fratre convenit.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (10, 566, 'Cobus Bester', 'bester.c@gmail.com', '', '', '2016-12-29 08:32:05', '2016-12-29 08:32:05', 'An dolor longissimus quisque miserrimus, voluptatem non optabiliorem diuturnitas facit? Sed quanta sit alias, nunc tantum possitne esse tanta. Scio enim esse quosdam, qui quavis lingua philosophari possint; Inde sermone vario sex illa a Dipylo stadia confecimus.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (11, 566, 'Tom McFarlin', 'tom@tommcfarlin.com', '', '', '2016-12-29 08:32:05', '2016-12-29 08:32:05', 'Quod autem in homine praestantissimum atque optimum est, id deseruit. Apud ceteros autem philosophos, qui quaesivit aliquid, tacet; Sedulo, inquam, faciam. Naturales divitias dixit parabiles esse, quod parvo esset natura contenta. Suo enim quisque studio maxime ducitur.', 0, 'post-trashed', '', 'comment', 0, 0);
INSERT INTO `wp_comments` VALUES (12, 568, 'James Koster', 'james@jameskoster.co.uk', '', '', '2016-12-29 08:32:06', '2016-12-29 08:32:06', 'Quod si ita se habeat, non possit beatam praestare vitam sapientia. Idemne, quod iucunde? Cur iustitia laudatur? Iam contemni non poteris.', 0, 'post-trashed', '', 'comment', 0, 0);
