INSERT INTO `wp_wc_admin_notes` VALUES (1, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-23 06:01:12', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (2, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Tips, product updates, and inspiration', 'We\'re here for you - get tips, product updates and inspiration straight to your email box', '{}', 'unactioned', 'woocommerce-admin', '2020-10-23 06:01:13', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (3, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Welcome to WooCommerce! Set up your store and start selling', 'We\'re here to help you going through the most important steps to get your store up and running.', '{}', 'actioned', 'woocommerce-admin', '2020-10-23 06:01:14', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (4, 'wc-admin-marketing-intro', 'info', 'en_US', 'Connect with your audience', 'Grow your customer base and increase your sales with marketing tools built for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-23 06:01:14', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (5, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2020-10-23 06:01:14', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (6, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'unactioned', 'woocommerce.com', '2020-10-23 06:07:23', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (7, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2020-10-23 06:01:15', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (8, 'wc-admin-draw-attention', 'info', 'en_US', 'How to draw attention to your online store', 'To get you started, here are seven ways to boost your sales and avoid getting drowned out by similar, mass-produced products competing for the same buyers.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-23 06:04:30', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (9, 'mailchimp-for-woocommerce-incomplete-install', 'warning', 'en_US', 'Mailchimp For WooCommerce', 'Plugin is not yet connected to a Mailchimp account. To complete the connection, open the settings page.', '{\"getting_started\":true,\"activated\":1603433250,\"activated_formatted\":\"October 23rd\"}', 'unactioned', 'mailchimp-for-woocommerce', '2020-10-23 06:07:30', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (10, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-24 04:01:54', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (11, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-27 00:12:50', NULL, 0, 'plain', '', 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (12, 'wc-admin-online-clothing-store', 'info', 'en_US', 'Start your online clothing store', 'Starting a fashion website is exciting but it may seem overwhelming as well. In this article, we\'ll walk you through the setup process, teach you to create successful product listings, and show you how to market to your ideal audience.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-27 00:12:51', NULL, 0, 'plain', '', 0, 'info');
